// ---------------
// BODY
// ---------------

* {
    box-sizing: border-box;

}

body {
    height: 100vh;
    background: radial-gradient(ellipse farthest-corner at center top, #ffee75, #ff8930);
    color: #8b9095;
    font-size: 14px;
}

@mixin center {
    position: relative;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}

// ---------------
// APP
// ---------------

.app {
    @include center;
    width: 640px;
    height: 390px;
    background: #fff;
    border-radius: 15px;
    box-shadow: inset 0 1px 0px rgba(0, 0, 0, 0.1);

    h2 {
        color: #525661;
        font-size: 17px;
        padding: 15px 0 0 28px;
    }
}

// ---------------
// HEADER
// ---------------

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 30px;
    height: 70px;
    border-bottom: 1px solid #D8D8D8;

    h1 {
        font-size: 20px;
        color: #1c222b;
        text-transform: uppercase;

        strong {
            display: inline-block;
            background: #F16018;
            color: #fff;
            border-radius: 15px;
            padding: 5px;
        }
    }

    .hamburger,
    .search {
        color: #363a47;
        font-size: 18px;
        cursor: pointer;
    }
}

// ---------------
// MOVIES
// ---------------

.movies {
    display: flex;
    padding: 8px 18px;

    .movie {
        padding: 10px;
        cursor: pointer;

        .poster {
            width: 100%;
            border-radius: 6px;
            margin-bottom: 6px;

            &.active {
                opacity: 0;
            }
        }

        .title {
            color: #525661;
            margin-bottom: 4px;
            font-size: 16px;
        }

        .info {
            font-size: 11px;
            opacity: 0.8;
        }

        .desc {
            display: none;
        }
    }

}

// ---------------
// DETAIL
// ---------------
.detail {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    padding: 37px 30px 30px 255px;
    z-index: 10;

    &::before {
        content: "";
        background: #fff;
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        border-radius: 15px;
        opacity: 0;
        z-index: -1;
        transition: all .4s cubic-bezier(.67, .13, .1, .81);
    }

    .close {
        position: absolute;
        top: 21px;
        right: 22px;
        font-size: 20px;
        cursor: pointer;
        // z-index: 10;
    }

    .poster {
        position: absolute;
        top: -10%;
        left: -6%;
        width: 45%;
        border-radius: 5px;
        box-shadow: 0 5px 30px rgba(0, 0, 0, .2);
        transition: all .5s cubic-bezier(.67, .13, .1, .81);
        // z-index: 2;
        opacity: 0;
    }

    .title,
    .info,
    .desc,
    .play,
    .close {
        transform: translateX(-50px);
        opacity: 0;
        transition: all .4s cubic-bezier(.67, .13, .1, .81);
    }

    .close {
        transform: translateX(10px);
    }

    .title {
        font-size: 35px;
        font-weight: bold;
        color: #525661;
        margin-bottom: 5px;
    }

    .info {
        font-size: 11px;
        opacity: 0;
        margin-left: 2px;
    }

    .desc {
        margin-top: 30px;
        line-height: 1.6;
    }

    .play {
        background: linear-gradient(90deg, #E4761F, #FF8B32);
        border: none;
        border-radius: 20px;
        padding: 8px 17px;
        line-height: 1.5;
        text-transform: uppercase;
        cursor: pointer;
        margin: 30px 0 0 -2px;
        letter-spacing: 0.5px;
        color: #fff;
        opacity: 0;

        i {
            margin-right: 8px;
        }

        &:hover {
            color: #4D2C12;
            transition: none;
        }
    }
}

.detail.ready {
    &::before {
        opacity: 1;
    }

    .info {
        opacity: .8;
    }

    .poster {
        opacity: 1;
        transition-duration: .5s;
    }

    .title,
    .info,
    .desc,
    .play,
    .close {
        transform: translateX(0);
        opacity: 1;
        transition-delay: 0s;
        transition-duration: .5s;
    }

    .title {
        transition-delay: .2s;
    }

    .info {
        transition-delay: .3s;
    }

    .desc {
        transition-delay: .4s;
    }

    .play {
        transition-delay: .5s;
    }
}
